import React from 'react'
import { Marker, Popup, MapContainer, TileLayer, useMap } from 'react-leaflet'
import { Icon } from "leaflet"
import useSwr from "swr"
import "../App.css"


const fetcher = (...args) => fetch(...args).then(response => response.json());

const icon = new Icon({
  iconUrl: "/icon.png",
  iconSize: [25, 25]
});

export default function App() {
  const url =
    "https://apis.datos.gob.ar/georef/api/provincias?nombre=Sgo.%20del%20Estero";
  const { data, error } = useSwr(url, fetcher);
  const personas = data && !error ? data.slice(0, 100) : [];
  const [posisionPersona, setPosisionPersona] = React.useState(null);

  return (
    <MapContainer center={[2.9345437183994125, -75.28091862671418]} zoom={15}>
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
      />

      {personas.map(ubicacion => (
        <Marker
          key={ubicacion.id}
          position={[ubicacion.location.lat, ubicacion.location.lon]}
          icon={icon}
          onClick={() => {
            setPosisionPersona(ubicacion);
          }}
        />
      ))}
    </MapContainer>
  );
}
