import React, { useState } from 'react';
import IconButton from '@mui/material/IconButton';
import FormHelperText from '@mui/material/FormHelperText';
import MapIcon from '@material-ui/icons/Map';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import Container from '@mui/material/Container';
import axios from 'axios';


function App() {
  // Declare a new state variable, which we'll call "count"
  const [state, setState] = useState({
    name: "",
    latitude: "",
    longitude: ""

  })

  const getPosition = () => {
    navigator.geolocation.getCurrentPosition((position) => {

      console.log(position.coords.latitude, position.coords.longitude);
      setState({
        latitude: position.coords.latitude,
        longitude: position.coords.longitude
      })

    })
  }

  const handleData = async (e) => {
    console.log(state)
    getPosition();
    valide();
  }


  const valide = () => {
    const payload = {
      "nombre": state.name,
      "latitude": state.latitude,
      "longitude": state.longitude,

    }
    console.log(payload)
    axios.post('http://localhost:5000/api/data', payload)
      .then(function (response) {
        console.log(response)
        if (response.status === 200) {
          alert("esta ok");
        }
        else if (response.status === 202) {
          alert("algo salio mal ");

        }

      })
      .catch(function (error) {
        alert('error')
        console.log(error)

      });
  }

  const handleChange = (e) => {
    const { id, value } = e.target
    setState(prevState => ({
      ...prevState,
      [id]: value
    }))

  }

  return (
    <Container>
      <IconButton variant="contained" color="black" href="/mapa" style={{ bottom: 10, left: -20 }}>
        <MapIcon style={{ fontSize: 50 }} color="lightBlue" />
        <FormHelperText id="standard-weight-helper-text">Mapa</FormHelperText>
      </IconButton>
      <Box>
        <br />
        <br />
        <br />
        <h1
          align='center'
        >GeoLand </h1>
        <br />
        <br />
        <br />
        <TextField
          fullWidth
          id="name"
          name="name"
          label="Nombre"
          defaultValue={state.name}
          onChange={handleChange}
          helperText="Con este nombre apareceras en el mapa"
        />
        <h1>{state.name} </h1>
        <Button
          fullWidth
          variant="contained"
          color="primary"
          onClick={() => handleData(state)}
        >
          iniciar
        </Button>
      </Box>
    </Container>
  );
}
export default App;
